// IMPORTANDO COMPONENTES
import database from './firebase.js';
import func , {_tasks} from './componentes/functions.js';
import Style from './componentes/style.js';

// CODIGO



export default function App() {

  var tasksRecivied = func.call(_tasks);

  return (


    <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>

      <View style={Style.header}>
        <Text style={{textAlign: 'center', fontSize: 17, fontWeight: 'bold'}}>APP - TASKS</Text>
      </View>

      {
        tasksRecivied.map((item)=>{
          return(
            <TouchableOpacity style={Style.task}>
              <Text style={{fontSize: 15, textTransform: 'capitalize', fontWeight: '500', textAling: 'center'}}>{item.task}</Text>
            </TouchableOpacity>
          )
        })
      }

    </View>

  );
}
