import React from 'react';
import {StyleSheet} from 'react-native';
import constants from 'expo-constants';

/*
#F6E5FC - plano de fundo
#D47ED8 - cor botao
#F6E5fC - texto botao
#E6CCE7- input color
#A864A8 - borda e texto
*/ 

export default StyleSheet.create({

    // HOME

    parentView:{
        height: '100%', 
        width: '100%'
    },

    header:{
        marginTop: constants.statusBarHeight,
    },

    backgroundView:{
        width: '100%',
        height: '100%',
        backgroundColor: '#F6E5FC',
    },
    imgStyle:{
        width: '40%', 
        height: '25%',
        alignSelf: 'center',
        marginTop: '30%'
    },

    loginInputs:{
        marginTop: '5%',
        width: '75%',
        height: 55,
        backgroundColor: '#E6CCE7',
        alignSelf: 'center',
        borderRadius: 15,
        borderWidth: 2,
        borderColor: '#A864A8',
        color: '#A864A8',
        textAlign: 'center',
        fontSize: 18,
    },

    loginBtn:{
        marginTop: '6%',
        width: '75%',
        height: 55,
        backgroundColor: '#D47ED8',
    }
   
})
