import React, {useState, useEffect} from 'react';
import { View, FlatList, TouchableOpacity, Text, TextInput,} from 'react-native';
import Style from './style';
import database from '../firebase';

function _tasks(){

  const [task, setTask] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(()=>{

    database.collection("tasks").onSnapshot((query)=>{
      const list = [];

      query.forEach((doc)=>{
        list.push({...doc.data(), id: doc.id});
      })
      setTask(list);
    })

  },[])

  function addTask(text){

    database.collection("tasks").add({task: text})

  }

  


  return task.map((val)=>{
    return(

      <View style={{backgroundColor: 'white', width: '100%', height: '50%'}}>
        <TouchableOpacity key={val.id} style={Style.task}>
          <Text style={{fontSize: 15, fontWeight: '500', color: 'black', textTransform: 'capitalize', textAlign: 'center',}}>{val.task}</Text>
        </TouchableOpacity>


        <TextInput style={{ textAlign: 'center',marginTop: '4%',backgroundColor: '#303030', width: '80%', height: 50, alignSelf: 'center', borderRadius: 20, color: 'white'}}
        
          onChangeText={(text)=> setNewTask(text)}
        />
          
        <TouchableOpacity style={Style.task} onPress={()=> addTask(newTask)}>
          <Text style={{fontSize: 15, fontWeight: '500', color: 'black', textTransform: 'capitalize', textAlign: 'center',}}>Adicionar tarefa</Text>
        </TouchableOpacity>

      </View>

    )
  })
}


export default _tasks;
