    const [task, setTask] = useState([
        
        {
          task: 'comprar pao'
        }, 

        {
            task: 'jogar o lixo'
        },
 
     ]);

     //const information = database.collection("tasks").doc("McuO7teNtaf6UuVsXiqp").get();

