import React from 'react';
import {StyleSheet} from 'react-native';
import constants from 'expo-constants';

/*
#F6E5FC - plano de fundo
#D47ED8 - cor botao
#F6E5FC - texto botao
#E6CCE7- input color
#A864A8 - borda e texto
#F3DBFB  - texto tabs
#832389 - cor tabs
*/ 

export default StyleSheet.create({

    // HOME

    parentView:{
        height: '100%', 
        width: '100%'
    },

    parentViewCad:{
        height: '84%', 
        width: '100%'
    },

    header:{
        marginTop: constants.statusBarHeight,
    },

    backgroundView:{
        width: '100%',
        height: '100%',
        backgroundColor: '#F6E5FC',
        alignItems: 'center',
    },
    imgStyle:{
        width: '40%', 
        height: '25%',
        alignSelf: 'center',
        marginTop: '30%'
    },

    inputs:{
        marginTop: '5%',
        width: '75%',
        height: 55,
        backgroundColor: '#E6CCE7',
        alignSelf: 'center',
        borderRadius: 25,
        borderWidth: 2,
        borderColor: '#A864A8',
        color: '#A864A8',
        textAlign: 'center',
        fontSize: 18,
    },

    loginBtn:{
        marginTop: '10%',
        width: '75%',
        height: 55,
        backgroundColor: '#D47ED8',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 25,
    },

    loginBtnTxt:{
        color: 'white',
        textAlign: 'center',
        fontSize: 18,
        fontWeight: '400',
        textTransform: 'capitalize',
    },

    // TELA DE CADASTRO

    cadHeader:{
        marginTop: constants.statusBarHeight,
        height: '8%',
        width: '100%',
        backgroundColor: '#832389',
        justifyContent: 'center',
        flexDirection: 'row'
    },
    cadHeaderTxt:{
        flex: 1,
        color: 'white',
        textTransform: 'uppercase',
        fontSize: 18,
        fontWeight: '600',
        textAlign: 'center',
        alignSelf: 'center',
        
    },
    btnLogout:{
        width: 40,
        height: 40,
        alignSelf: 'center',
        marginRight: '2%',
        alignItems: 'center',
        justifyContent: 'center',
    },
    logoutIcon:{
        alignSelf:'center',
        color: 'white',
    },
    label:{
        marginTop: '30%',
        color: '#A864A8',
        textTransform: 'uppercase',
        fontWeight: '500',
        fontSize: 25,
    },
    btnNavigate:{
        
        
        width: '100%',
        height: '8%',
        backgroundColor: '#832389',
    },
    btnNavigateTxt:{

    }

   
})
