//#endregion exemplos

// IMPORTANDO COMPONENTES
import CadScreen from './componentes/functions.js';
import Style from './componentes/style.js';

// CODIGO

export default function App() {

  const [screen, setScreen] = useState('home');

  if(screen == 'home'){

    return(
      <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>

        <View style={Style.header}>
          <Text style={{textAlign: 'center', fontSize: 17, fontWeight: 'bold'}}>HOME</Text>
        </View>

        <TouchableOpacity onPress={()=> setScreen('cad')}>
          <Text> IR PARA O CADASTRO</Text>
        </TouchableOpacity>


        <TouchableOpacity onPress={()=> setScreen('view')}>
          <Text> IR PARA A VISUALIZAÇÃO</Text>
        </TouchableOpacity>



      </View>
    );

  }
  else if(screen == 'cad'){
    return(
      <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>

        <View style={Style.header}>
          <Text style={{textAlign: 'center', fontSize: 17, fontWeight: 'bold'}}>APP - CRUD</Text>
        </View>


        <CadScreen></CadScreen>


      </View>
    )
  }
  else if(screen == 'view'){

    return(

        <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>

            <View style={Style.header}>
              <Text style={{textAlign: 'center', fontSize: 17, fontWeight: 'bold'}}>VISUALIZAR</Text>
            </View>

          <TouchableOpacity onPress={()=> setScreen('edit')}>
            <Text> CLIQUE PARA VER MAIS INFORMAÇÕES</Text>
          </TouchableOpacity>

        </View>
    ) 
  }
  else if(screen == 'edit'){
    return(
      <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>

        <View style={Style.header}>
          <Text style={{textAlign: 'center', fontSize: 17, fontWeight: 'bold'}}>EDITAR</Text>
        </View>

      </View>
    )
  }



}
