import React, {useState, useEffect} from 'react';
import { View, FlatList, TouchableOpacity, Text, TextInput, ScrollView} from 'react-native';
import Style from './style';
import database from '../firebase';

function Tasks(){

  const [user, setUser] = useState([]);
  
  const [newName, setNewTask]           = useState('');
  const [newPassword, setNewPassowrd]   = useState('');
  const [newId, setNewId]               = useState('');

  useEffect(()=>{

    database.collection("users").onSnapshot((query)=>{
      const list = [];

      query.forEach((doc)=>{
        list.push({...doc.data(), id: doc.id});
      })
      setUser(list);
    })

  },[])

  function addTask(text){

    database.collection("users").add({task: text})

  }

  function deleteTask(id){
    database.collection("users").doc(id).delete()

  }

  function view(){
    return user.map((val)=>{
          
      return <View key={val.id} style={{marginTop: '2%',alignSelf: 'center',width: '90%', height: 100, backgroundColor: 'black', alignItems: 'center'}}>
        
        

        <Text style={{textDecorationLine: 'underline', textDecorationColor: 'white',marginTop: '4%',textAlign: 'center',color: 'white'}}>{val.name}</Text>
        <Text style={{textDecorationLine: 'underline', textDecorationColor: 'white',marginTop: '4%',textAlign: 'center',color: 'white'}}>{val.password}</Text>
        <Text style={{textDecorationLine: 'underline', textDecorationColor: 'white',marginTop: '4%',textAlign: 'center',color: 'white'}}>{val.login}</Text>

        <TouchableOpacity style={{alignSelf: 'center',alignItems: 'center',marginTop: '5%',backgroundColor: '#a83244', width: 40, height: 20}}
          onPress={()=>deleteTask(val.id)}
        >
          <Text style={{color: 'white',textAlign: 'center'}}>X</Text>
        </TouchableOpacity>

      </View>
    })
  }

  return (
    <View style={{width: '100%', height: '80%', backgroundColor: 'white'}}>
     {
       <ScrollView>
        {view()}
       </ScrollView>
     }


     <TextInput style={{textAlign: 'center',alignSelf: 'center',borderWidth: 1, borderColor: 'black' ,backgroundColor: 'white', width: '80%', height: '10%',marginTop: '4%'}}
     
      onChangeText={(text)=> setNewTask(text)}
      placeholder="Escreva sua nova tarefa aqui:"
     />

     <TouchableOpacity style={{marginTop:'4%',alignSelf: 'center',justifyContent: 'center',alignItems: 'center',backgroundColor: '#32a852', width: '70%', height: '10%'}}
      onPress={()=>addTask(newTask)}
     >
       <Text style={{color: 'white', fontSize: 16,}}> Adicionar Tarefa!</Text>
     </TouchableOpacity>

    </View>
  )
}


