import func , {_handler} from './componentes/functions.js';
import Header from './componentes/Header.js';

// CODIGO

export default function App() {

  const [tasks,setTasks] = useState([

    {
      key: 1,
      task: "comprar pao"
    },
    {
      key: 2,
      task: "react native"
    },
    {
      key: 3,
      task: "tarefinha"
    },

  ])

  var mao = func.call(_handler.i);

  return (

    <View style={{width: '100%', height: '100%', backgroundColor: '#CCC'}}>

      <Header valor={mao}></Header>

      {
        tasks.map((valor)=>{
          return(
            <View style={{width: 100, height: 100, backgroundColor: 'blue'}}key={valor.key}>
              <Text style={{color: 'black'}}>
                {
                  valor.task
                }
              </Text>
            </View>
          )

        })

      }

    </View>

  );
}
