import React from 'react';
import {StyleSheet} from 'react-native';
import constants from 'expo-constants';

/*
#F6E5FC - plano de fundo
#D47ED8 - cor botao
#F6E5fC - texto botao
#E6CCE7- input color
#A864A8 - borda e texto
#F3DBfB  - texto tabs
#832389 - cor tabs
*/ 

export default StyleSheet.create({

    // HOME

    parentView:{
        height: '100%', 
        width: '100%'
    },

    header:{
        marginTop: constants.statusBarHeight,
    },

    backgroundView:{
        width: '100%',
        height: '100%',
        backgroundColor: '#F6E5FC',
        alignItems: 'center',
    },
    imgStyle:{
        width: '40%', 
        height: '25%',
        alignSelf: 'center',
        marginTop: '30%'
    },

    loginInputs:{
        marginTop: '5%',
        width: '75%',
        height: 55,
        backgroundColor: '#E6CCE7',
        alignSelf: 'center',
        borderRadius: 25,
        borderWidth: 2,
        borderColor: '#A864A8',
        color: '#A864A8',
        textAlign: 'center',
        fontSize: 18,
    },

    loginBtn:{
        marginTop: '10%',
        width: '75%',
        height: 55,
        backgroundColor: '#D47ED8',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 25,
    },

    loginBtnTxt:{
        color: 'white',
        textAlign: 'center',
        fontSize: 18,
        fontWeight: '400',
        textTransform: 'capitalize',
    },

    // TELA DE CADASTRO

    cadHeader:{
        marginTop: constants.statusBarHeight,
        height: '8%',
        width: '100%',
        backgroundColor: '#832389',
    },
    cadHeaderTxt:{
        color: 'white',
        textTransform: 'uppercase',
        fontSize: 20,
        fontWeight: '600',
    }
   
})
