import React, {useState, useEffect} from 'react';
import { View, FlatList, TouchableOpacity, Text, TextInput, ScrollView, Alert} from 'react-native';
import Style from './style';
import database from '../firebase';

function Tasks(){

  const [user, setUser] = useState([]);
  
  const [newName, setNewName]           = useState('');
  const [newPassword, setNewPassowrd]   = useState('');
  const [newId, setNewId]               = useState('');

  useEffect(()=>{

    database.collection("users").onSnapshot((query)=>{
      const list = [];

      query.forEach((doc)=>{
        list.push({...doc.data(), id: doc.id});
      })
      setUser(list);
    })
    
  },[])


  function addUser(name,password,login){

    database.collection("users").add({name: name, password: password, login: login})

  }

  return (
    <View style={{width: '100%', height: '80%', backgroundColor: 'white'}}>
    
     <TextInput style={{textAlign: 'center',alignSelf: 'center',borderWidth: 1, borderColor: 'black' ,backgroundColor: 'white', width: '80%', height: '10%',marginTop: '4%'}}
     
      onChangeText={(text)=> setNewName(text)}
      placeholder="Escreva seu nome aqui:"
     />

      <TextInput style={{textAlign: 'center',alignSelf: 'center',borderWidth: 1, borderColor: 'black' ,backgroundColor: 'white', width: '80%', height: '10%',marginTop: '4%'}}
      secureTextEntry={true}
      onChangeText={(text)=> setNewPassowrd(text)}
      placeholder="Escreva sua senha aqui:"
      />

      <TextInput style={{textAlign: 'center',alignSelf: 'center',borderWidth: 1, borderColor: 'black' ,backgroundColor: 'white', width: '80%', height: '10%',marginTop: '4%'}}
     
      onChangeText={(text)=> setNewId(text)}
      placeholder="Escreva seu login:"
      />

     <TouchableOpacity style={{marginTop:'4%',alignSelf: 'center',justifyContent: 'center',alignItems: 'center',backgroundColor: '#32a852', width: '70%', height: '10%'}}
      onPress={()=>addUser(newName, newPassword, newId)}
     >
       <Text style={{color: 'white', fontSize: 16,}}> Adicionar Usu√°rio!</Text>
     </TouchableOpacity>

    </View>
  )
}


