import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, TextInput, View, TouchableOpacity, useColorScheme } from 'react-native';

// IMPORTANDO COMPONENTES

import Header from './componentes/Header.js';

// CODIGO

export default function App() {

  const [tasks,setTasks] = useState([

    {key: 1, task: "comprar pão"},
    {key: 2, task: "estudar react native"},
    {key: 3, task: "criar firebase"},
    {key: 4, task: "ver o crud de pam-II"}

  ]);

  function loop(){

    tasks.map((element)=>{
      <View key={element.key} style={{width: 100, height: 100}}>
        <Text>{element.task}</Text>
      </View>
    })

  }

  return (
    <View style={{width: '100%', height: '100%', backgroundColor: '#CCC'}}>

      <Header></Header>

      <View>{loop()}</View>

    </View>
  );
}
