// IMPORTANDO COMPONENTES

import Header from './componentes/Header.js';

// CODIGO

export default function App() {

  const [tasks,setTasks] = useState([

    {
      key: 1,
      task: "comprar pao"
    },
    {
      key: 2,
      task: "react native"
    },
    {
      key: 3,
      task: "tarefinha"
    },

  ])

  function Looping(){
    tasks.map((valor)=>{

      return(
        <View key={valor.key}>
          <Text style={{color: 'black'}}>
            {
              valor.task
            }
          </Text>
        </View>
      )
    })
  }

  return (
    <View style={{width: '100%', height: '100%', backgroundColor: '#CCC'}}>

      <Header></Header>

        {
          Looping()

        }
    </View>
  );
}
