//#endregion exemplos

// IMPORTANDO COMPONENTES
import database from './firebase';

import CadScreen from './componentes/functions.js';
import Style from './componentes/style.js';
import img from './componentes/firebase.png';

// CODIGO

export default function App() {

  const [user,setUser] = useState([]);

  const [currentPassowrd, setCPassword] = useState('');

  useEffect(()=>{
      database.collection("users").onSnapshot((query)=>{

        const list = [];
        query.forEach((doc)=>{
        list.push({...doc.data(), id: doc.id});
      })
      setUser(list);
    })

  },[])

  function verifyUser(id){
    if(currentPassowrd == user["password"] ){
      alert('A senha é igual');
    }
    else{
      alert("ops")
    }
  }

  const [screen, setScreen] = useState('home');

  if(screen == 'home'){

    return(
      <View style={Style.parentView}>

        <View style={Style.header}></View>

        <View style={Style.backgroundView}>

          <Image style={Style.imgStyle}source={img}></Image>

          <TextInput placeholder="Insira seu código" placeholderTextColor="#A864A8" style={Style.loginInputs}/>
          <TextInput onChangeText={(text)=> setCPassword(text)} placeholder="Insira sua senha" placeholderTextColor="#A864A8" style={Style.loginInputs}/>

          <TouchableOpacity onPress={()=> verifyUser()}style={Style.loginBtn}>
            <Text style={Style.loginBtnTxt}>Log-in</Text>
          </TouchableOpacity>

        </View>

      </View>

    );

  }
  else if(screen == 'cad'){
    return(
      <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>

        <View style={Style.header}></View>

          <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>
            <Text style={{textAlign: 'center', fontSize: 17, fontWeight: 'bold'}}>CADASTRO</Text>
        </View>


        <CadScreen></CadScreen>


      </View>
    )
  }
  else if(screen == 'view'){

    return(

        <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>

          <View style={Style.header}></View>

          <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>
            <Text style={{textAlign: 'center', fontSize: 17, fontWeight: 'bold'}}>VISUALIZAR</Text>
          </View>

          <TouchableOpacity onPress={()=> setScreen('edit')}>
            <Text> CLIQUE PARA VER MAIS INFORMAÇÕES</Text>
          </TouchableOpacity>

        </View>
    ) 
  }
  else if(screen == 'edit'){
    return(
      <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>

        <View style={Style.header}></View>

        <View style={{width: '100%', height: '100%', backgroundColor: '#303030'}}>
            <Text style={{textAlign: 'center', fontSize: 17, fontWeight: 'bold'}}>CADASTRO</Text>
        </View>

      </View>
    )
  }



}
