import React, {useState, useEffect} from 'react';
import { View, FlatList, TouchableOpacity, Text, TextInput,} from 'react-native';
import Style from './style';
import database from '../firebase';

function Tasks(){

  const [task, setTask] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(()=>{

    database.collection("tasks").onSnapshot((query)=>{
      const list = [];

      query.forEach((doc)=>{
        list.push({...doc.data(), id: doc.id});
      })
      setTask(list);
    })

  },[])

  function addTask(text){

    database.collection("tasks").add({task: text})

  }

  function deleteTask(id){
    database.collection("tasks").doc(id).delete()
  }

  function view(){
    return task.map((val)=>{
          
      return <View key={val.id} style={{marginTop: '2%',alignSelf: 'center',width: '90%', height: '20%', backgroundColor: 'black', alignItems: 'center'}}>
        
        

        <Text style={{textDecorationLine: 'underline', textDecorationColor: 'white',marginTop: '4%',textAlign: 'center',color: 'white'}}>{val.task}</Text>

        <TouchableOpacity style={{marginTop: '5%',backgroundColor: '#a83244', width: 40, height: 40}}>
          <Text>X</Text>
        </TouchableOpacity>

      </View>
    })
  }

  return (
    <View style={{width: '100%', height: '80%', backgroundColor: 'white'}}>
     {
       view()
     }


     <TextInput style={{textAlign: 'center',alignSelf: 'center',borderWidth: 1, borderColor: 'black' ,backgroundColor: 'white', width: '80%', height: '10%',marginTop: '4%'}}
     
      onChangeText={(text)=> setNewTask(text)}
      placeholder="Escreva sua nova tarefa aqui:"
     />

     <TouchableOpacity style={{marginTop:'4%',alignSelf: 'center',justifyContent: 'center',alignItems: 'center',backgroundColor: '#32a852', width: '70%', height: '10%'}}
      onPress={()=>addTask(newTask)}
     >
       <Text style={{color: 'white', fontSize: 16,}}> Adicionar Tarefa!</Text>
     </TouchableOpacity>

    </View>
  )
}


